import { Component, OnInit } from '@angular/core';
import { ApiService } from '../api.service';
import { Theme } from '../types/theme';

@Component({
  selector: 'app-themes-list',
  templateUrl: './themes-list.component.html',
  styleUrls: ['./themes-list.component.css'],
})
export class ThemesListComponent implements OnInit {
  themes: Theme[] = [];
  isLoading: boolean = true;
  constructor(private api: ApiService) {}
  ngOnInit(): void {
    this.api.getThemes().subscribe({
      next: (themes) => {
        console.log(themes);
        this.themes = themes;
        this.isLoading = false;
      },
      error: (err) => {
        console.log('Error: ' + err);
      },
    });
  }
}
<main>
  <div *ngIf="isLoading">
    <app-loader />
  </div>
  <div *ngIf="!isLoading" class="theme-title">
    <!-- theme component  -->
    <div *ngFor="let theme of themes" class="theme-container">
      <div class="theme-name-wrapper">
        <div class="theme-name">
          <a href="#" class="normal">
            <h2>{{ theme.themeName }}</h2>
          </a>
          <div class="columns">
            <div>
              <p>
                Date: <time>{{ theme.created_at }}</time>
              </p>
              <div class="nick-name">
                <p>
                  Username: <span>{{ theme.userId.username }}</span>
                </p>
              </div>
            </div>
            <div class="subscribers">
              <!-- <button class="subscribe">Subscribe</button> -->
              <p>
                Subscribers: <span>{{ theme.subscribers?.length }}</span>
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</main>
